import AMF.Util.Object

class RecordSet:

    Con = None
    
    def __init__(self, Con):
        self.Con = Con
    
    
    def query(self, queryText):
    
        Cursor = self.Con.cursor()
        Cursor.execute(queryText)
        
        o = AMF.Util.Object.Object()
        o.pseudo_query(Cursor)
        return o
        
